let requestQueue = flow.get('request_queue') || [];
let pendingQueue = flow.get('pending_queue') || [];

// Add status to each item to indicate state for frontend
let combinedQueue = [];

// Requests waiting to be accepted/rejected
combinedQueue = combinedQueue.concat(
    requestQueue.map(item => ({ ...item, status: 'pending' }))
);

// Requests accepted, waiting to be completed
combinedQueue = combinedQueue.concat(
    pendingQueue.map(item => ({ ...item, status: 'accepted' }))
);

msg.payload = combinedQueue;
return msg;

