let queue = flow.get('request_queue') || [];
let pending = flow.get('pending_queue') || [];
const { machine } = msg.payload;

if (!machine) {
    msg.statusCode = 400;
    msg.payload = { error: "Missing machine field" };
    return msg;
}

const acceptedMachine = queue.find(item => item.machine === machine);
if (!acceptedMachine) {
    msg.statusCode = 404;
    msg.payload = { error: "Machine not found in queue" };
    return msg;
}

// Remove from request queue
queue = queue.filter(item => item.machine !== machine);
flow.set('request_queue', queue);

// Save to pending queue
pending.push({
    machine: acceptedMachine.machine,
    requested_time: acceptedMachine.timestamp,
    accepted_time: new Date().toISOString()
});
flow.set('pending_queue', pending);

msg.statusCode = 200;
msg.payload = { Result: 'Accept', machine };
return msg;

let pending = flow.get('pending_queue') || [];

const machine = msg.payload.machine;
const requestTimeStr = msg.payload.requestTime;
const requestTime = new Date(requestTimeStr);

if (!machine || isNaN(requestTime.getTime())) {
    node.error("Invalid data for completing request");
    return null;
}

const index = pending.findIndex(item => item.machine === machine && item.requested_time === requestTimeStr);
if (index === -1) {
    node.warn("Machine not found in pending queue");
    return null;
}

pending.splice(index, 1); // Remove from pending queue
flow.set('pending_queue', pending);

const completeTime = new Date();
const duration = completeTime.getTime() - requestTime.getTime();

// Helper function to format date as 'YYYY-MM-DD HH:mm:ss'
function formatDate(date) {
    const pad = n => n.toString().padStart(2, '0');
    return `${date.getFullYear()}-${pad(date.getMonth() + 1)}-${pad(date.getDate())} ` +
        `${pad(date.getHours())}:${pad(date.getMinutes())}:${pad(date.getSeconds())}`;
}

