let data = msg.payload;

if (!Array.isArray(data) || data.length === 0) {
    node.warn("No duration data found.");
    return null;
}

let total = 0;
let min = data[0];
let max = data[0];

data.forEach(item => {
    // Convert ms to seconds with 2 decimals
    item.duration_sec = +(Number(item.duration) / 1000).toFixed(2);
    total += item.duration_sec;

    if (item.duration_sec < Number(min.duration) / 1000) min = item;
    if (item.duration_sec > Number(max.duration) / 1000) max = item;
});

const avg = +(total / data.length).toFixed(2);

// Prepare labels and values in seconds
const labels = data.map(item => item.machine);
const values = data.map(item => item.duration_sec);

msg.payload = {
    average_sec: avg,  // number in seconds
    chart: {
        labels: labels,
        values: values
    },
    shortest: `${(Number(min.duration) / 1000).toFixed(2)} s (Machine: ${min.machine})`,
    longest: `${(Number(max.duration) / 1000).toFixed(2)} s (Machine: ${max.machine})`
};

return msg;

