// Prepare machine buckets
let machines = { M1: [], M2: [], M3: [], M4: [] };

msg.payload.forEach(point => {
    let machine = point.machine || (point.tags && point.tags.machine);
    let duration = point._value || point.duration;

    if (machines[machine]) {
        machines[machine].push(duration);
    }
});

function average(arr) {
    if (arr.length === 0) return 0;
    return arr.reduce((a, b) => a + b, 0) / arr.length;
}

function stdDev(arr) {
    if (arr.length === 0) return 0;
    let avg = average(arr);
    let squareDiffs = arr.map(value => (value - avg) ** 2);
    let avgSquareDiff = average(squareDiffs);
    return Math.sqrt(avgSquareDiff);
}

// Build stats object with values in seconds (ms / 1000), fixed to 2 decimals
let stats = {};
for (let m in machines) {
    stats[m] = {
        average: (average(machines[m]) / 1000).toFixed(2),
        stddev: (stdDev(machines[m]) / 1000).toFixed(2)
    };
}

msg.payload = stats;
// Stringify for injecting in template safely
msg.payloadAsJson = JSON.stringify(stats);

return msg;

